svg.tb:metadata:
{
  "title": "My first SCADA Object",
  "initial": [
     {
       "tag": "RECT",
       "attributes": [
          {
             "name": "fill",
             "value": {
                 "type": "property",
                 "propertyId": "background"
             }
          },
          {
             "name": "stroke",
             "value": {
                 "type": "property",
                 "propertyId": "strokeColor"
             }
          },
          {
             "name": "stroke-width",
             "value": {
                "type": "property",
                "propertyId": "strokeWidth"
             }
          }
       ]
     }
  ],
  "behavior": [
     {
       "id": "initialState",
       "name": "Initial state",
       "type": "getValue",
       "valueType": "DOUBLE",
       "defaultValue": 0,
       "onUpdate": [
          {
             "matcher": {
               "type": "any"
             },
             "state": [
                {
                   "tag": "RECT",
                   "animate": 200,
                   "attributes": [
                      {
                         "name": "height",
                         "value": {
                             "type": "input"
                         }
                      },
                      {
                         "name": "width",
                         "value": {
                             "type": "input"
                         }
                      }
                   ]
                }
             ]
          }
       ]
     },
     {
        "id": "disabledState",
        "name": "Disabled state",
        "type": "getValue",
        "valueType": "BOOLEAN",
        "defaultValue": false,
        "onUpdate": [
           {
             "matcher": {
                 "type": "constant",
                 "value": true
              },
              "state": [
                 {
                   "tag": "RECT",
                   "attributes": [
                      {
                         "name": "fill",
                         "value": {
                             "type": "property",
                             "propertyId": "disabledBackground"
                         }
                      }
                   ]
                 }
              ]
           },
           {
             "matcher": {
                 "type": "constant",
                 "value": false
              },
              "state": [
                 {
                   "tag": "RECT",
                   "attributes": [
                      {
                         "name": "fill",
                         "value": {
                             "type": "property",
                             "propertyId": "background"
                         }
                      }
                   ]
                 }
              ]
           }
        ]
     }
  ],
  "properties": [
      {
         "id": "strokeColor",
         "name": "Stroke color",
         "type": "color",
         "default": "#aaa"
      },
      {
         "id": "strokeWidth",
         "name": "Stroke width",
         "type": "number",
         "default": 5,
         "min": 0,
         "max": 20
      },
      {
        "id": "background",
        "name": "Background",
        "type": "color",
        "default": "green"
     },
     {
       "id": "disabledBackground",
       "name": "Disabled background",
       "type": "color",
       "default": "#ccc"
     }
  ]
}
